<script lang="ts">
  const buttons = [
    { label: 'zabiegi' },
    { label: 'cennik' },
    //{ label: 'kursy' },
    { label: 'bellery', href: 'https://allegro.pl/uzytkownik/bellery' },
    { label: 'instagram', href: 'https://www.instagram.com/bellbeauty.pl/' },
    { label: 'kontakt' }
  ] as {
    label: string
    href: string | null
  }[]

  const mobileNavigaitonGrid = `1fr repeat(${buttons.length}, max-content) 1fr`

  let mobileMenuVisible = false
</script>

<style lang="scss">
  .navigation {
    z-index: 100;
    position: sticky;
    top: 0;
    border-bottom: 2px solid #000000;
    height: 80px;
    background: #ffffff;

    > .wrapper {
      display: grid;
      grid-template-columns: 1fr max-content;
      align-items: center;
      padding: 0 30px;
      height: 100%;
    }

    &__logo {
      margin: 10px 0 0;
      width: fit-content;

      > svg {
        display: block;
      }
    }

    &__buttons {
      display: grid;
      grid-auto-columns: max-content;
      gap: 50px;
      text-transform: uppercase;

      > a {
        grid-row: 1;
        transition: color .2s;

        &:hover, &:focus {
          color: $accent;
        }
      }
    }

    &__toggle {
      display: none;
    }
